import React from 'react';
import { IconType } from 'react-icons';
import { Tooltip } from 'react-tooltip';
import 'react-tooltip/dist/react-tooltip.css';

interface ButtonProps {
  label: string;
  backgroundColors?: string[];
  size?: 'small' | 'medium' | 'large';
  icon?: IconType;
  iconPosition?: 'left' | 'right';
  tooltip?: string;
  tooltipPosition?: 'top' | 'bottom' | 'right' | 'left';
  onClick?: () => void;
}

const Button: React.FC<ButtonProps> = ({
  label,
  backgroundColors = ['#007BFF', '#6C757D', '#28A745'],
  size = 'medium',
  icon: Icon,
  iconPosition = 'left',
  tooltip,
  tooltipPosition = 'top',
  onClick,
}) => {
  const sizeStyles = {
    small: 'py-1 px-2 text-sm',
    medium: 'py-2 px-4 text-base',
    large: 'py-3 px-6 text-lg',
  };

  const backgroundStyle = `bg-gradient-to-r from-${backgroundColors[0]} via-${backgroundColors[1]} to-${backgroundColors[2]}`;

  return (
    <>
      <button
        id="button-tooltip"
        className={`inline-flex items-center justify-center ${sizeStyles[size]} ${backgroundStyle} text-white font-semibold rounded shadow hover:opacity-90 focus:outline-none transition duration-200`}
        onClick={onClick}
      >
        {iconPosition === 'left' && Icon && <Icon className="mr-2" />}
        {label}
        {iconPosition === 'right' && Icon && <Icon className="ml-2" />}
      </button>
      {tooltip && (
        <Tooltip
          anchorId="button-tooltip"
          content={tooltip}
          place={tooltipPosition}
        />
      )}
    </>
  );
};

export default Button;
